import { useAuth } from '../context/AuthContext';
import "../styles/ManagerDashboardStyle.css";

const ManagerDashboard = () => {
    const { user } = useAuth();

    return (
        <div className="background-container">
            <div className="decorative-image-1"></div>
            <div className="decorative-image-2"></div>

            <div className="content">
                <div className="user-info">
                    <div className="avatar-container">
                        <div className="avatar-circle">
                            {user?.firstname?.charAt(0)}{user?.lastname?.charAt(0)}
                        </div>
                    </div>
                    <div className="user-details">
                        <div className="user-name">
                            {user?.firstname} {user?.lastname}
                        </div>
                        <div className="user-role">
                            {user?.role === 'manager' ? 'Менеджер' : user?.role}
                        </div>
                    </div>
                    <div className="dop-info">
                        {user?.department && (
                            <div className="department-info">
                                <span className="info-label">Департамент:</span>
                                <span className="info-value">{user.department}</span>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ManagerDashboard;
