import React, { useState, useEffect } from 'react';
import { getPropertiesByUser } from '../api/propertyService';
import PropertyCard from './PropertyCard';
import '../styles/PropertyList.css';
import {useAuth} from "../context/AuthContext";

const PropertyList = ({ managerId }) => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { user } = useAuth();

    useEffect(() => {
        const fetchProperties = async () => {
            if (!managerId) return;

            try {
                const data = await getPropertiesByUser(managerId);
                setProperties(data);
            } catch (err) {
                setError('Ошибка загрузки списка недвижимости');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchProperties();
    }, [user.managerId]); // Добавили зависимость от managerId, чтобы запрос обновлялся при изменении менеджера

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="property-list">
            {properties.length > 0 ? (
                properties.map(property => (
                    <PropertyCard key={property.id} property={property} />
                ))
            ) : (
                <div className="no-properties">Недвижимость не найдена</div>
            )}
        </div>
    );
};

export default PropertyList;
