import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { getManagerByUserId } from '../api/managerService';
import "../styles/ManagerDashboardStyle.css";

const ManagerDashboard = () => {
    const { user } = useAuth();
    const [manager, setManager] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (user?.id) {
            const fetchManagerData = async () => {
                try {
                    const managerData = await getManagerByUserId(user.id);
                    setManager(managerData);
                    console.log(managerData.department); // Используем managerData вместо manager

                } catch (err) {
                    setError('Ошибка загрузки данных менеджера');
                    console.error(err);
                } finally {
                    setLoading(false);
                }
            };

            fetchManagerData();
        }
    }, [user?.id]);

    if (loading) return <div>Загрузка...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="background-container">
            <div className="decorative-image-1"></div>
            <div className="decorative-image-2"></div>

            <div className="content">
                <div className="user-info">
                    <div className="user-info-container">
                        <div className="avatar-container">
                            <div className="avatar-circle">
                                {user?.firstname?.charAt(0)}{user?.lastname?.charAt(0)}
                            </div>
                        </div>
                        <div className="user-name">
                            <div className="user-name">
                                {user?.firstname} {user?.lastname}
                            </div>
                            <div className="user-role">
                                {user?.role === 'manager' ? 'Менеджер' : user?.role}
                            </div>
                        </div>
                    </div>

                    <div className="dop-info">
                        {manager?.department && (
                            <div className="manager-info">
                                Отдел: {manager.department}
                            </div>
                        )}
                        <div className="manager-info">
                            {user.email}
                        </div>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default ManagerDashboard;
