import React, { useEffect, useState } from 'react';
import Slider from "react-slick";  // импортируем слайдер
import { useParams, Link, useNavigate } from 'react-router-dom';
import { getPropertyById, deleteProperty } from '../api/propertyService';
import { getClientById, getClientsByManager } from '../api/ClientService';
import { useAuth } from '../context/AuthContext';
import { createTravelRequest, grantAccessToProperty } from "../api/accessProperty";
import "../styles/PropertyDetailPage.css"
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const PropertyDetailPage = () => {
    const { id } = useParams();
    const { user } = useAuth();
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [clients, setClients] = useState([]);
    const [selectedClientId, setSelectedClientId] = useState('');
    const [clientData, setClientData] = useState(null);
    const isManager = user?.role === 'MANAGER';
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProperty = async () => {
            try {
                const data = await getPropertyById(id);
                setProperty(data);
            } catch (err) {
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };
        fetchProperty();
    }, [id]);

    useEffect(() => {
        if (isManager && user?.id) {
            const fetchClients = async () => {
                try {
                    const data = await getClientsByManager(user.id);
                    setClients(data);
                } catch (err) {
                    console.error('Ошибка при получении клиентов:', err);
                }
            };
            fetchClients();
        }
    }, [isManager, user]);

    useEffect(() => {
        const fetchClientData = async () => {
            const clientId = localStorage.getItem('clientId');
            if (clientId) {
                try {
                    const data = await getClientById(clientId);
                    setClientData(data);
                } catch (err) {
                    console.error('Ошибка при получении данных клиента:', err);
                }
            }
        };
        fetchClientData();
    }, []);

    const handleClientChange = (e) => setSelectedClientId(e.target.value);

    const handleAssign = async () => {
        if (!selectedClientId || !property?.id || !user?.id) return;
        const managerId = user.id;
        try {
            await grantAccessToProperty(managerId, selectedClientId, property.id);
            alert('Доступ успешно выдан клиенту!');
        } catch {
            alert('Ошибка при выдаче доступа');
        }
    };

    const handleDelete = async () => {
        if (!window.confirm("Вы уверены, что хотите удалить это помещение?")) return;
        try {
            await deleteProperty(property.id);
            alert('Помещение успешно удалено');
            navigate('/properties');
        } catch (error) {
            alert('Ошибка при удалении помещения');
            console.error(error);
        }
    };

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    // Настройки слайдера
    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        arrows: true,
    };

    return (
        <div>
            <button className="back-button" onClick={() => navigate(-1)}>← Назад</button>
            <div className="property-detail-page">
                <h1>Детали недвижимости</h1>

                {/* Слайдер с изображениями */}
                {Array.isArray(property.imagePaths) && property.imagePaths.length > 0 && (
                    <Slider {...sliderSettings} className="property-image-slider">
                        {property.imagePaths.map((img, index) => (
                            <div key={index}>
                                <img
                                    src={`http://localhost:8080/uploads/${img}`}
                                    alt={`${property.title} - ${index + 1}`}
                                    style={{ maxWidth: '100%', height: 'auto', marginBottom: '20px' }}
                                />
                            </div>
                        ))}
                    </Slider>
                )}

                {property ? (
                    <div>
                        <h2>{property.title}</h2>
                        <p>Город: {property.address.city}</p>
                        <p>Район: {property.address.district}</p>
                        <p>Улица: {property.address.street}</p>
                        <p>Цена: {property.price}</p>
                        <p>Количество комнат: {property.rooms}</p>
                        <p>Этаж: {property.floor}</p>
                        <p>Балкон: {property.hasBalcony ? 'Да' : 'Нет'}</p>
                        <p>Тип: {property.propertyType}</p>
                        <p>Животные: {property.allowPets ? 'Разрешены' : 'Запрещены'}</p>

                        {isManager && (
                            <div className="form-group" style={{ marginTop: '20px' }}>
                                <label>Клиент:</label>
                                <select value={selectedClientId} onChange={handleClientChange} required>
                                    <option value="">-- Выберите клиента --</option>
                                    {clients.map(client => (
                                        <option key={client.id} value={client.id}>
                                            {client.authUser
                                                ? `${client.authUser.lastname} ${client.authUser.firstname}`
                                                : `Клиент #${client.id}`}
                                        </option>
                                    ))}
                                </select>
                                <button onClick={handleAssign} disabled={!selectedClientId}>
                                    Присвоить
                                </button>
                            </div>
                        )}

                        {!isManager && clientData?.accessLevel === 'TRAVELER' && property?.propertyLevel === 'TRAVEL' && (
                            <div style={{ marginTop: '20px' }}>
                                <button onClick={async () => {
                                    try {
                                        await createTravelRequest(clientData.id, property.id);
                                        alert('Запрос успешно отправлен');
                                    } catch {
                                        alert('Ошибка при отправке запроса');
                                    }
                                }}>
                                    Отправить запрос
                                </button>
                            </div>
                        )}

                        {isManager && (
                            <>
                                <Link to={`/properties/${id}/edit`}>
                                    <button>Редактировать</button>
                                </Link>
                                <button className="delete-button" onClick={handleDelete}>
                                    Удалить помещение
                                </button>
                            </>
                        )}
                    </div>
                ) : (
                    <p>Информация о недвижимости не найдена</p>
                )}
            </div>
        </div>
    );
};

export default PropertyDetailPage;
