import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {getPropertyById, updateProperty} from '../api/propertyService';
import {getClientById, getClientsByManager} from '../api/ClientService'; // Импортируем функцию API
import { useAuth } from '../context/AuthContext';
import {createTravelRequest, grantAccessToProperty} from "../api/accessProperty";

const PropertyDetailPage = () => {
    const { id } = useParams();
    const { user } = useAuth(); // Получаем текущего пользователя
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [clients, setClients] = useState([]); // Список клиентов
    const [selectedClientId, setSelectedClientId] = useState('');
    const [clientData, setClientData] = useState(null);

    const isManager = user?.role === 'MANAGER';
    const [editableProperty, setEditableProperty] = useState({
        title: '',
        address: '',
        price: 0,
        rooms: 0,
        floor: 0,
        hasBalcony: false,
        allowPets: false,
        propertyLevel: '',
        description: '',
        propertyType: '',
        status: ''
    });
    useEffect(() => {
        const fetchProperty = async () => {
            try {
                const data = await getPropertyById(id);
                setProperty(data);
            } catch (err) {
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };

        fetchProperty();
    }, [id]);

    useEffect(() => {
        if (isManager && user?.id) {
            const fetchClients = async () => {
                try {
                    const data = await getClientsByManager(user.id);
                    setClients(data);
                } catch (err) {
                    console.error('Ошибка при получении клиентов:', err);
                }
            };
            fetchClients();
        }
    }, [isManager, user]);

    useEffect(() => {
        const fetchClientData = async () => {
            const clientId = localStorage.getItem('clientId');
            if (clientId) {
                try {
                    const data = await getClientById(clientId);
                    setClientData(data);
                } catch (err) {
                    console.error('Ошибка при получении данных клиента:', err);
                }
            }
        };

        fetchClientData();
    }, []);

    // Handle form input change
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setEditableProperty({
            ...editableProperty,
            [name]: type === 'checkbox' ? checked : value
        });
    };

    const handleClientChange = (e) => {
        setSelectedClientId(e.target.value);
    };

    const handleAssign = async () => {
        if (!selectedClientId || !property?.id || !user?.id) return;
        const managerId = user.id;

        try {
            // Вызываем grantAccessToProperty с параметрами managerId, clientId и propertyId
            await grantAccessToProperty(managerId, selectedClientId, property.id);
            alert('Доступ успешно выдан клиенту!');
        } catch (error) {
            alert('Ошибка при выдаче доступа');
        }
    };
    // Handle update property
    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const updatedProperty = await updateProperty(id, editableProperty);
            setProperty(updatedProperty); // Обновляем отображаемую информацию
            alert('Данные недвижимости успешно обновлены');
        } catch (error) {
            alert('Ошибка при обновлении данных');
        }
    };

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="property-detail-page">
            <h1>Детали недвижимости</h1>
            {property ? (
                <div>
                    <h2>{property.title}</h2>
                    <p>Город: {property.city}</p>
                    <p>Район: {property.district}</p>
                    <p>Улица: {property.street}</p>
                    <p>Цена: {property.price}</p>
                    <p>Количество комнат: {property.rooms}</p>
                    <p>Этаж: {property.floor}</p>
                    <p>Балкон: {property.hasBalcony ? 'Да' : 'Нет'}</p>
                    <p>Тип: {property.propertyType}</p>
                    <p>Животные: {property.allowPets ? 'Разрешены' : 'Запрещены'}</p>


                    {/* Форма для редактирования недвижимости */}
                    <form onSubmit={handleUpdate}>
                        <h3>Редактировать недвижимость</h3>
                        <div>
                            <label>Заголовок:</label>
                            <input
                                type="text"
                                name="title"
                                value={editableProperty.city}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Адрес:</label>
                            <input
                                type="text"
                                name="address"
                                value={editableProperty.address}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Цена:</label>
                            <input
                                type="number"
                                name="price"
                                value={editableProperty.price}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Количество комнат:</label>
                            <input
                                type="number"
                                name="rooms"
                                value={editableProperty.rooms}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Этаж:</label>
                            <input
                                type="number"
                                name="floor"
                                value={editableProperty.floor}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Балкон:</label>
                            <input
                                type="checkbox"
                                name="hasBalcony"
                                checked={editableProperty.hasBalcony}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label>Разрешены животные:</label>
                            <input
                                type="checkbox"
                                name="allowPets"
                                checked={editableProperty.allowPets}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label>Уровень недвижимости:</label>
                            <input
                                type="text"
                                name="propertyLevel"
                                value={editableProperty.propertyLevel}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Описание:</label>
                            <textarea
                                name="description"
                                value={editableProperty.description}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Тип недвижимости:</label>
                            <input
                                type="text"
                                name="propertyType"
                                value={editableProperty.propertyType}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Статус:</label>
                            <input
                                type="text"
                                name="status"
                                value={editableProperty.status}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <button type="submit">Сохранить изменения</button>
                    </form>

                    {isManager && (
                        <div className="form-group" style={{ marginTop: '20px' }}>
                            <label>Клиент:</label>
                            <select
                                value={selectedClientId}
                                onChange={handleClientChange}
                                required
                            >
                                <option value="">-- Выберите клиента --</option>
                                {clients.map(client => (
                                    <option key={client.id} value={client.id}>
                                        {client.authUser
                                            ? `${client.authUser.lastname} ${client.authUser.firstname}`
                                            : `Клиент #${client.id}`}
                                    </option>
                                ))}
                            </select>
                            <button onClick={handleAssign} disabled={!selectedClientId}>
                                Присвоить
                            </button>
                        </div>
                    )}

                    {!isManager && clientData?.accessLevel === 'TRAVELER' && property?.propertyLevel === 'TRAVEL' && (
                        <div style={{ marginTop: '20px' }}>
                            <button onClick={async () => {
                                try {
                                    await createTravelRequest(clientData.id, property.id);
                                    alert('Запрос успешно отправлен');
                                } catch (e) {
                                    alert('Ошибка при отправке запроса');
                                }
                            }}>
                                Отправить запрос
                            </button>
                        </div>
                    )}

                </div>
            ) : (
                <p>Информация о недвижимости не найдена</p>
            )}
        </div>
    );
};

export default PropertyDetailPage;
