import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';
import { ROLES } from '../../constants/roles';
import './Header.css'; // Стили для компонента

const Header = () => {
    const { isAuthenticated, user, logout } = useAuth();

    return (
        <header className="header">
            <div className="header-container">
                <h1 className="header-title">Agency App</h1>
                <nav className="header-nav">
                    {isAuthenticated ? (
                        <div className="header-auth-container">
                            {user?.role === ROLES.ADMIN && (
                                <Link to="/admin-dashboard" className="header-link">
                                    Админ
                                </Link>
                            )}
                            {user?.role === ROLES.MANAGER && (
                                <Link to="/manager-dashboard" className="header-link">
                                    Менеджер
                                </Link>
                            )}
                            {user?.role === ROLES.CLIENT && (
                                <Link to="/client-dashboard" className="header-link">
                                    Клиент
                                </Link>
                            )}
                            <span className="header-user">
                                {user?.firstname} {user?.lastname}
                            </span>
                            <button onClick={logout} className="header-button">
                                Выйти
                            </button>
                        </div>
                    ) : (
                        <Link to="/login" className="header-link">
                            Вход
                        </Link>
                    )}
                </nav>
            </div>
        </header>
    );
};

export default Header;
