import React, { useEffect, useState } from 'react';
import { getClientsByManager, getHousingPreferences  } from '../api/ClientService'; // путь адаптируйте под свой проект
import "../styles/ClientList.css"
const ClientList = ({ managerId }) => {
    const [clients, setClients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [expandedClient, setExpandedClient] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const clientsData = await getClientsByManager(managerId);
                // Добавляем поле для хранения предпочтений
                const clientsWithPreferences = clientsData.map(client => ({
                    ...client,
                    preferences: null,
                    loadingPreferences: false
                }));
                setClients(clientsWithPreferences);
            } catch (err) {
                setError('Ошибка при загрузке клиентов');
            } finally {
                setLoading(false);
            }
        };

        if (managerId) {
            fetchData();
        }
    }, [managerId]);

    const loadPreferences = async (clientId) => {
        setClients(prevClients =>
            prevClients.map(client =>
                client.id === clientId
                    ? { ...client, loadingPreferences: true }
                    : client
            )
        );

        try {
            const preferences = await getHousingPreferences(clientId);
            setClients(prevClients =>
                prevClients.map(client =>
                    client.id === clientId
                        ? { ...client, preferences, loadingPreferences: false }
                        : client
                )
            );
        } catch (err) {
            setClients(prevClients =>
                prevClients.map(client =>
                    client.id === clientId
                        ? {
                            ...client,
                            preferences: [],
                            loadingPreferences: false
                        }
                        : client
                )
            );
        }
    };

    const toggleClientPreferences = (clientId) => {
        if (expandedClient === clientId) {
            setExpandedClient(null);
        } else {
            setExpandedClient(clientId);
            const client = clients.find(c => c.id === clientId);
            if (!client.preferences) {
                loadPreferences(clientId);
            }
        }
    };

    if (loading) return <div className="loading">Загрузка клиентов...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="clients-preferences-table">
            <h2>Клиенты и их предпочтения</h2>
            <table>
                <thead>
                <tr>
                    <th>Информация о клиенте</th>
                    <th>Предпочтения по жилью</th>
                </tr>
                </thead>
                <tbody>
                {clients.map(client => (
                    <React.Fragment key={client.id}>
                        <tr
                            className="client-row"
                            onClick={() => toggleClientPreferences(client.id)}
                        >
                            <button onClick={(e) => {
                                e.stopPropagation(); // чтобы не сработал toggleClientPreferences
                                setEditingClient(client);
                            }}>Редактировать</button>

                            <td>
                                <div><strong>Телефон:</strong> {client.phone}</div>
                                <div><strong>Паспорт:</strong> {client.passport || 'не указан'}</div>
                                <div><strong>Уровень доступа:</strong> {client.accessLevel}</div>
                            </td>
                            <td>
                                {client.loadingPreferences ? (
                                    <div>Загрузка предпочтений...</div>
                                ) : (
                                    <div>
                                        {client.preferences
                                            ? `Показано ${client.preferences.length} предпочтений`
                                            : 'Нажмите для загрузки предпочтений'}
                                    </div>
                                )}
                            </td>
                        </tr>
                        {expandedClient === client.id && client.preferences && (
                            <tr className="preferences-details">
                                <td colSpan="2">
                                    {client.preferences.length > 0 ? (
                                        client.preferences.map((pref, index) => (
                                            <div key={index} className="preference-item">
                                                <h4>Предпочтение #{index + 1}</h4>
                                                <div className="preference-details">
                                                    <div><strong>Город:</strong> {pref.city || 'не указан'}</div>
                                                    <div><strong>Район:</strong> {pref.district || 'не указан'}</div>
                                                    <div><strong>Улица:</strong> {pref.street || 'не указана'}</div>
                                                    <div><strong>Площадь:</strong> {pref.minArea || '?'} - {pref.maxArea || '?'} м²</div>
                                                    <div><strong>Цена:</strong> {pref.minPrice || '?'} - {pref.maxPrice || '?'}</div>
                                                    <div><strong>Комнат:</strong> {pref.roomsCount || 'не указано'}</div>
                                                    <div><strong>Балкон:</strong> {pref.hasBalcony ? 'да' : 'нет'}</div>
                                                    <div><strong>Животные:</strong> {pref.allowPets ? 'разрешены' : 'не разрешены'}</div>
                                                    <div><strong>Примечания:</strong> {pref.additionalNotes || 'нет'}</div>
                                                </div>
                                            </div>
                                        ))
                                    ) : (
                                        <div>Нет данных о предпочтениях</div>
                                    )}
                                </td>
                            </tr>
                        )}
                    </React.Fragment>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default ClientList;
