import React, { useState, useEffect } from 'react';
import { register } from '../api/authApi';
import { useAuth } from '../context/AuthContext';
import "../styles/CreatePropertyForm.css";

const RegisterUser = () => {
    const { user } = useAuth();
    const [formData, setFormData] = useState({
        firstname: '',
        lastname: '',
        patronymic: '',
        email: '',
        password: '',
        role: 'CLIENT',
        phone: '',
        passport: '',
        accessLevel: 'BASIC',
        department: ''
    });

    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [allowedRoles, setAllowedRoles] = useState(['CLIENT']);

    useEffect(() => {
        if (!user) return;

        // Безопасный доступ к ролям (проверяем наличие user.roles)
        const userRoles = user.roles || [];

        if (userRoles.includes('ADMIN')) {
            setAllowedRoles(['CLIENT', 'MANAGER', 'ADMIN']);
        } else if (userRoles.includes('MANAGER')) {
            setAllowedRoles(['CLIENT']);
            setFormData(prev => ({ ...prev, role: 'CLIENT' }));
        }
    }, [user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Проверка прав с безопасным доступом к ролям
        const userRoles = user?.roles || [];
        if (!user || userRoles.includes('CLIENT')) {
            setError('У вас нет прав для создания пользователей');
            return;
        }

        setLoading(true);
        setError('');

        try {
            await register(formData);
            // Очистка формы после успешной регистрации
            setFormData({
                firstname: '',
                lastname: '',
                patronymic: '',
                email: '',
                password: '',
                role: allowedRoles[0],
                phone: '',
                passport: '',
                accessLevel: 'BASIC',
                department: ''
            });
        } catch (err) {
            setError(err.message || 'Ошибка регистрации');
        } finally {
            setLoading(false);
        }
    };

    // Если пользователь не авторизован или это клиент
    const userRoles = user?.roles || [];
    if (!user || userRoles.includes('CLIENT')) {
        return (
            <div className="register-container">
                <h2>Регистрация пользователя</h2>
                <div className="error-message">У вас нет прав для создания пользователей</div>
            </div>
        );
    }

    return (
        <div className="register-container">
            <h2>Регистрация пользователя</h2>
            {error && <div className="error-message">{error}</div>}

            <form onSubmit={handleSubmit} className="register-form">
                {/* Все поля формы остаются без изменений */}

                <div className="form-group">
                    <label>Роль:</label>
                    <select
                        name="role"
                        value={formData.role}
                        onChange={handleChange}
                        required
                        disabled={allowedRoles.length === 1}
                    >
                        {allowedRoles.map(role => (
                            <option key={role} value={role}>
                                {role === 'CLIENT' && 'Клиент'}
                                {role === 'MANAGER' && 'Менеджер'}
                                {role === 'ADMIN' && 'Администратор'}
                            </option>
                        ))}
                    </select>
                </div>

                {formData.role === 'MANAGER' && (
                    <div className="form-group">
                        <label>Отдел:</label>
                        <input
                            type="text"
                            name="department"
                            value={formData.department}
                            onChange={handleChange}
                            required={formData.role === 'MANAGER'}
                        />
                    </div>
                )}

                <button type="submit" disabled={loading}>
                    {loading ? 'Загрузка...' : 'Зарегистрировать'}
                </button>
            </form>
        </div>
    );
};

export default RegisterUser;
