// PreferenceEditModal.js (simplified version)
import React, { useState } from 'react';

const PreferenceEditModal = ({ preference, onSave, onCancel }) => {
    const [updatedPreference, setUpdatedPreference] = useState(preference);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedPreference({ ...updatedPreference, [name]: value });
    };

    const handleSave = () => {
        onSave(updatedPreference); // Save the changes
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <h3>Редактирование предпочтений</h3>
                <label>
                    Город:
                    <input
                        type="text"
                        name="city"
                        value={updatedPreference.city}
                        onChange={handleChange}
                    />
                </label>
                <div className="modal-buttons">
                    <button onClick={handleSave}>Сохранить</button>
                    <button onClick={onCancel}>Отмена</button>
                </div>
            </div>
        </div>
    );
};

export default PreferenceEditModal;
