import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getPropertyById } from '../api/propertyService';
import {getClientById, getClientsByManager} from '../api/ClientService'; // Импортируем функцию API
import { useAuth } from '../context/AuthContext';
import {createTravelRequest, grantAccessToProperty} from "../api/accessProperty";
import { Link } from 'react-router-dom';
import "../styles/PropertyDetailPage.css"
import { useNavigate } from 'react-router-dom';
import { deleteProperty } from '../api/propertyService'; // Импортируй delete
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import ImageSlider from "../components/Layout/ImageSlider";

const PropertyDetailPage = () => {
    const { id } = useParams();
    const { user } = useAuth(); // Получаем текущего пользователя
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [clients, setClients] = useState([]); // Список клиентов
    const [selectedClientId, setSelectedClientId] = useState('');
    const [clientData, setClientData] = useState(null);
    const isManager = user?.role === 'MANAGER';
    const navigate = useNavigate();

    const handleDelete = async () => {
        const confirmed = window.confirm("Вы уверены, что хотите удалить это помещение?");
        if (!confirmed) return;

        try {
            await deleteProperty(property.id);
            alert('Помещение успешно удалено');
            navigate('/properties'); // Перенаправление, подкорректируй под свой роутинг
        } catch (error) {
            alert('Ошибка при удалении помещения');
            console.error(error);
        }
    };

    useEffect(() => {
        const fetchProperty = async () => {
            try {
                const data = await getPropertyById(id);
                setProperty(data);
                console.log('Image path:', data.imagePaths);

            } catch (err) {
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };

        fetchProperty();
    }, [id]);

    useEffect(() => {
        if (isManager && user?.id) {
            const fetchClients = async () => {
                try {
                    const data = await getClientsByManager(user.id);
                    setClients(data);
                } catch (err) {
                    console.error('Ошибка при получении клиентов:', err);
                }
            };
            fetchClients();
        }
    }, [isManager, user]);

    useEffect(() => {
        const fetchClientData = async () => {
            const clientId = localStorage.getItem('clientId');
            if (clientId) {
                try {
                    const data = await getClientById(clientId);
                    setClientData(data);
                } catch (err) {
                    console.error('Ошибка при получении данных клиента:', err);
                }
            }
        };

        fetchClientData();
    }, []);


    const handleClientChange = (e) => {
        setSelectedClientId(e.target.value);
    };

    const handleAssign = async () => {
        if (!selectedClientId || !property?.id || !user?.id) return;
        const managerId = user.id;

        try {
            // Вызываем grantAccessToProperty с параметрами managerId, clientId и propertyId
            await grantAccessToProperty(managerId, selectedClientId, property.id);
            alert('Доступ успешно выдан клиенту!');
        } catch (error) {
            alert('Ошибка при выдаче доступа');
        }
    };


    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="property-detail">
            <button className="back-button" onClick={() => navigate(-1)}>← Назад</button>

            <div className="property-header">
                <h1>{property.title}</h1>
                <p className="property-type">{property.propertyType}</p>
            </div>

            {property.imagePaths?.length > 0 && (
                <div className="property-slider">
                    <ImageSlider images={property.imagePaths} />
                </div>
            )}

            <div className="property-info">
                <p><strong>Город:</strong> {property.address.city}</p>
                <p><strong>Район:</strong> {property.address.district}</p>
                <p><strong>Улица:</strong> {property.address.street}</p>
                <p><strong>Цена:</strong> {property.price} ₽</p>
                <p><strong>Комнат:</strong> {property.rooms}</p>
                <p><strong>Этаж:</strong> {property.floor}</p>
                <p><strong>Балкон:</strong> {property.hasBalcony ? 'Да' : 'Нет'}</p>
                <p><strong>Животные:</strong> {property.allowPets ? 'Разрешены' : 'Запрещены'}</p>
            </div>

            {isManager && (
                <div className="assign-client">
                    <label>Выдать доступ клиенту:</label>
                    <select value={selectedClientId} onChange={e => setSelectedClientId(e.target.value)}>
                        <option value="">-- Выберите клиента --</option>
                        {clients.map(client => (
                            <option key={client.id} value={client.id}>
                                {client.authUser
                                    ? `${client.authUser.lastname} ${client.authUser.firstname}`
                                    : `Клиент #${client.id}`}
                            </option>
                        ))}
                    </select>
                    <button disabled={!selectedClientId} onClick={handleAssign}>
                        Присвоить
                    </button>
                </div>
            )}

            {!isManager && clientData?.accessLevel === 'TRAVELER' && property?.propertyLevel === 'TRAVEL' && (
                <div className="travel-request">
                    <button onClick={async () => {
                        try {
                            await createTravelRequest(clientData.id, property.id);
                            alert('Запрос успешно отправлен');
                        } catch {
                            alert('Ошибка при отправке запроса');
                        }
                    }}>
                        Отправить запрос
                    </button>
                </div>
            )}

            {isManager && (
                <div className="action-buttons">
                    <Link to={`/properties/${id}/edit`} className="edit-button">Редактировать</Link>
                    <button onClick={handleDelete} className="delete-button">Удалить помещение</button>
                </div>
            )}
        </div>
    );
};

export default PropertyDetailPage;
