import api from './axiosConfig';

export const getAllProperties = async () => {
    const response = await api.get('/properties');
    return response.data;
};
export const getPropertiesByUser = async (managerId) => {
    try {
        // Отправляем запрос с переданным ID менеджера
        const response = await api.get(`/manager/${managerId}/properties`);

        // Возвращаем полученные данные
        return response.data;
    } catch (error) {
        // Обработка ошибки, если запрос не удался
        console.error("Error fetching properties: ", error);
        throw error;
    }
};

export const getPropertyById = async (id) => {
    const response = await api.get(`/properties/${id}`);
    return response.data;
};
export const createProperty = async (propertyData) => {
    const response = await api.post('/properties', propertyData);
    return response.data;
};

// Получение enum значений с бэка (если нужно)
export const getPropertyEnums = async () => {
    const response = await api.get('/properties/enums');
    return response.data;
};
