import React, { useState } from 'react';

const PreferenceEditModal = ({ preferencesData, onSave, onClose }) => {
    const [editedData, setEditedData] = useState(preferencesData);

    // Обработчик изменения данных в форме
    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    // Обработчик сохранения данных
    const handleSave = () => {
        onSave(editedData); // Вызываем handleEditPreferences с новыми данными
        onClose(); // Закрываем модальное окно
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Редактировать предпочтение</h2>
                <form>
                    <label>
                        Город:
                        <input
                            type="text"
                            name="city"
                            value={editedData?.city || ''}
                            onChange={handleChange}
                        />
                    </label>
                    <label>
                        Район:
                        <input
                            type="text"
                            name="district"
                            value={editedData?.district || ''}
                            onChange={handleChange}
                        />
                    </label>
                    <label><input>Улица:</input> {editedData?.street || 'не указана'}</label>
                    <label><input>Площадь:</input> {editedData?.minArea || '?'} - {editedData?.maxArea || '?'} м²</label>
                    <label><input>Цена:</input> {editedData?.minPrice || '?'} - {editedData?.maxPrice || '?'}</label>
                    <label><input>Комнат:</input> {editedData?.roomsCount || 'не указано'}</label>
                    <label><input>Балкон:</input> {editedData?.hasBalcony ? 'да' : 'нет'}</label>
                    <label><input>Животные:</input> {editedData?.allowPets ? 'разрешены' : 'нет'}</label>
                    <label><input>Примечания:</input> {editedData?.additionalNotes || 'нет'}</label>
                    {/* Добавьте остальные поля по аналогии */}
                    <button type="button" onClick={handleSave}>Сохранить</button>
                    <button type="button" onClick={onClose}>Закрыть</button>
                </form>
            </div>
        </div>
    );
};

export default PreferenceEditModal;
