import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { getPropertyById } from '../api/propertyService';
import { AuthContext } from '../context/AuthContext'; // Предполагаем, что у вас есть AuthContext

const PropertyDetailPage = () => {
    const { id } = useParams();
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { user } = useContext(AuthContext); // Получаем пользователя из контекста

    useEffect(() => {
        const fetchProperty = async () => {
            setLoading(true);
            setError(null);
            try {
                const data = await getPropertyById(id);
                setProperty(data);
            } catch (err) {
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };
        fetchProperty();
    }, [id]);

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    // Условие для отображения кнопки и поля выбора клиента
    const isManager = user?.role === 'manager'; // Допустим, роль хранится в поле 'role'

    return (
        <div className="property-detail-page">
            <h1>Детали недвижимости</h1>
            {property ? (
                <div>
                    <h2>{property.title}</h2>
                    <p>Город: {property.city}</p>
                    <p>Район: {property.district}</p>
                    <p>Улица: {property.street}</p>
                    <p>Цена: {property.price}</p>
                    <p>Количество комнат: {property.rooms}</p>
                    <p>Тип недвижимости: {property.floor}</p>
                    <p>Балкон: {property.hasBalcony}</p>
                    <p>Тип: {property.propertyType}</p>
                    <p>Животные: {property.allowPets}</p>

                    {isManager && (
                        <div>
                            <h3>Выберите клиента</h3>
                            <select>
                                <option value="">Выберите клиента</option>
                                {/* Здесь может быть список клиентов */}
                                <option value="client1">Клиент 1</option>
                                <option value="client2">Клиент 2</option>
                                {/* И так далее */}
                            </select>

                            <button onClick={() => console.log('Действие с выбранным клиентом')}>
                                Присвоить
                            </button>
                        </div>
                    )}
                </div>
            ) : (
                <p>Информация о недвижимости не найдена</p>
            )}
        </div>
    );
};

export default PropertyDetailPage;
