import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getPropertyById } from '../api/propertyService';
import { useAuth } from '../context/AuthContext'; // Подключаем хук useAuth

const PropertyDetailPage = () => {
    const { id } = useParams();
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { user } = useAuth(); // Получаем данные пользователя из контекста

    useEffect(() => {
        const fetchProperty = async () => {
            setLoading(true);
            setError(null);
            try {
                const data = await getPropertyById(id);
                setProperty(data);
            } catch (err) {
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };
        fetchProperty();
    }, [id]);

    const isManager = user?.role === 'ROLE_MANAGER'; // Проверь точное значение роли

    const [selectedClientId, setSelectedClientId] = useState('');
    const handleAssign = () => {
        console.log(`Назначен клиент ID: ${selectedClientId} на недвижимость ID: ${property.id}`);
        // Здесь будет API-запрос, например: assignPropertyToClient(property.id, selectedClientId)
    };

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="property-detail-page">
            <h1>Детали недвижимости</h1>
            {property ? (
                <div>
                    <h2>{property.title}</h2>
                    <p>Город: {property.city}</p>
                    <p>Район: {property.district}</p>
                    <p>Улица: {property.street}</p>
                    <p>Цена: {property.price}</p>
                    <p>Количество комнат: {property.rooms}</p>
                    <p>Этаж: {property.floor}</p>
                    <p>Балкон: {property.hasBalcony ? 'Да' : 'Нет'}</p>
                    <p>Тип: {property.propertyType}</p>
                    <p>Животные: {property.allowPets ? 'Разрешены' : 'Запрещены'}</p>

                    {isManager && (
                        <div style={{ marginTop: '20px' }}>
                            <h3>Назначить клиента</h3>
                            <select
                                value={selectedClientId}
                                onChange={(e) => setSelectedClientId(e.target.value)}
                            >
                                <option value="">Выберите клиента</option>
                                <option value="1">Клиент 1</option>
                                <option value="2">Клиент 2</option>
                                {/* Здесь динамически можно подгрузить список клиентов */}
                            </select>
                            <button onClick={handleAssign} disabled={!selectedClientId}>
                                Присвоить
                            </button>
                        </div>
                    )}
                </div>
            ) : (
                <p>Информация о недвижимости не найдена</p>
            )}
        </div>
    );
};

export default PropertyDetailPage;
