import React, { useState } from 'react';
import '../authStyles/LoginForm.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm  = ({ setToken, setUserRole, setUserId }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:8080/api/auth/signin', {
                email,
                password
            });
            setToken(response.data.token);
            localStorage.setItem('token', response.data.token);

            const userResponse = await axios.get('http://localhost:8080/api/user/me', {
                headers: {
                    Authorization: `Bearer ${response.data.token}`
                }
            });

            const userId = userResponse.data.id;
            setUserRole(userResponse.data.role);
            setUserId(userId);

            console.log('User ID:', userId);

            if (userResponse.data.role === 'MANAGER') {
                navigate('/dashboard');
            } else if (userResponse.data.role === 'ADMIN') {
                navigate('/dashboard');
            } else if (userResponse.data.role === 'CLIENT') {
                navigate('/dashboard');
            }else {
                navigate('/dashboard');
            }
        } catch (error) {
            setError('Invalid email or password');
        }
    };

    return (
        <div className="wrapper">
            <div className="container">
                <h1>Авторизация</h1>
                {error && <div className="error-message">{error}</div>}

                <form className="form" onSubmit={handleSubmit}>
                    <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />

                    <button type="submit" id="login-button">Войти</button>
                </form>
            </div>
            <ul className="bg-bubbles">
                {[...Array(10)].map((_, i) => (
                    <li key={i}></li>
                ))}
            </ul>
        </div>
    );
};

export default LoginForm;
