import api from "./axiosConfig";

// Функция для выдачи доступа
export const grantAccessToProperty = async (managerId, clientId, propertyId) => {
    try {
        // Отправляем запрос с параметрами в теле запроса
        const response = await api.post('/access/grant', {
            managerId,
            clientId,
            propertyId
        });
        return response.data;
    } catch (error) {
        console.error("Ошибка при выдаче доступа:", error);
        throw error;
    }
};
