import React, { useState, useEffect } from 'react';
import PropertyCard from './PropertyCard';
import { getPropertiesByManagerId } from '../api/propertyService';
import '../styles/PropertyList.css';
import ImageSlider from "./Layout/ImageSlider";

const PropertyList = ({ manager }) => {
    const [properties, setProperties] = useState([]);

    useEffect(() => {
        const fetchProperties = async () => {
            if (manager?.id) {
                const data = await getPropertiesByManagerId(manager.id);
                // console.log(data);
                setProperties(data);
            }
        };
        fetchProperties();
    }, [manager]);

    return (
        <div className="property-list">
            <ImageSlider
                dots={true}
                infinite={true}
                speed={500}
                slidesToShow={1}
                slidesToScroll={1}
                arrows={false}
                className="mobile-info-slider"
            >
            {properties.length > 0 ? (
                properties.map(property => (
                    <PropertyCard key={property.id} property={property} />
                ))
            ) : (
                <div className="no-properties">Недвижимость не найдена</div>
            )}
            </ImageSlider>

        </div>
    );
};

export default PropertyList;
