// components/PreferenceEditModal.js

import React, { useState } from 'react';

const PreferenceEditModal = ({ preference, onSave, onCancel }) => {
    const [updatedPreference, setUpdatedPreference] = useState(preference);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedPreference((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(updatedPreference); // Передаем обновленные данные обратно в родительский компонент
    };

    return (
        <div className="modal">
            <form onSubmit={handleSubmit}>
                <label>
                    Город:
                    <input
                        type="text"
                        name="city"
                        value={updatedPreference.city}
                        onChange={handleChange}
                    />
                </label>
                <button type="submit">Сохранить</button>
                <button type="button" onClick={onCancel}>Отмена</button>
            </form>
        </div>
    );
};

export default PreferenceEditModal;
