import React, { useEffect, useState } from 'react';
import { getAllProperties } from '../api/propertyApi'; // убедись, что путь правильный
import PropertyCard from '../components/PropertyCard'; // путь тоже проверь
import '../styles/CatalogPage.css'; // при необходимости добавь стили

const CatalogPage = () => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProperties = async () => {
            try {
                const data = await getAllProperties();
                setProperties(data);
            } catch (err) {
                console.error('Ошибка при загрузке объектов:', err);
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };

        fetchProperties();
    }, []);

    if (loading) return <div className="loader">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="page-container">
            <h1>Каталог</h1>
            <div className="property-list">
                {properties.length > 0 ? (
                    properties.map((property) => (
                        <PropertyCard key={property.id} property={property} />
                    ))
                ) : (
                    <p>Объекты недвижимости не найдены.</p>
                )}
            </div>
        </div>
    );
};

export default CatalogPage;
