// PreferenceEditModal.js (simplified version)
import React, { useState } from 'react';

const PreferenceEditModal = ({ preference, onSave, onCancel }) => {
    const [updatedPreference, setUpdatedPreference] = useState(preference);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedPreference({ ...updatedPreference, [name]: value });
    };

    const handleSave = () => {
        onSave(updatedPreference); // Save the changes
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <div className="preference-item">

                    <div><strong>Город:</strong> {preference.cyty || 'не указан'}</div>
                    <div><strong>Район:</strong> {preference.district || 'не указан'}</div>
                    <div><strong>Улица:</strong> {preference.street || 'не указана'}</div>
                    <div><strong>Площадь:</strong> {preference.minArea || '?'} - {preference.maxArea || '?'} м²</div>
                    <div><strong>Цена:</strong> {preference.minPrice || '?'} - {preference.maxPrice || '?'}</div>
                    <div><strong>Комнат:</strong> {preference.roomsCount || 'не указано'}</div>
                    <div><strong>Балкон:</strong> {preference.hasBalcony ? 'да' : 'нет'}</div>
                    <div><strong>Животные:</strong> {preference.allowPets ? 'разрешены' : 'нет'}</div>
                    <div><strong>Примечания:</strong> {preference.additionalNotes || 'нет'}</div>
                </div>
            </div>
        </div>
    );
};

export default PreferenceEditModal;
