import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { createProperty } from '../api/propertyService';
import '../styles/CreatePropertyForm.css';

const CreatePropertyForm = ({ onSuccess }) => {
    const { user } = useAuth();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        address: '',
        description: '',
        price: '',
        rooms: '',
        floor: ''
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const payload = {
                ...formData,
                rooms: parseInt(formData.rooms),
                price: parseFloat(formData.price),
                floor: parseInt(formData.floor),
                userId: user.id // предполагается, что ID пользователя передаётся
            };

            await createProperty(payload);

            // Очистка формы
            setFormData({
                address: '',
                description: '',
                price: '',
                rooms: '',
                floor: ''
            });

            // Обновление данных менеджера на дашборде
            if (onSuccess) {
                await onSuccess();
            }

        } catch (err) {
            setError(err.response?.data?.message || 'Ошибка при создании недвижимости');
            console.error('Ошибка:', err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <form className="property-form" onSubmit={handleSubmit}>
            <h2>Добавить недвижимость</h2>

            {error && <div className="form-error">{error}</div>}

            <input
                type="text"
                name="address"
                placeholder="Адрес"
                value={formData.address}
                onChange={handleChange}
                required
            />

            <input
                type="text"
                name="description"
                placeholder="Описание"
                value={formData.description}
                onChange={handleChange}
                required
            />

            <input
                type="number"
                name="price"
                placeholder="Цена"
                value={formData.price}
                onChange={handleChange}
                required
            />

            <input
                type="number"
                name="rooms"
                placeholder="Количество комнат"
                value={formData.rooms}
                onChange={handleChange}
                required
            />

            <input
                type="number"
                name="floor"
                placeholder="Этаж"
                value={formData.floor}
                onChange={handleChange}
                required
            />

            <button type="submit" disabled={loading}>
                {loading ? 'Сохранение...' : 'Создать'}
            </button>
        </form>
    );
};

export default CreatePropertyForm;
