import React, { useState } from 'react';
import { utils, writeFile } from 'xlsx';
import { getClientsByManager, getHousingPreferences, getUserByClientId } from '../api/ClientService';

const ExcelReportButton = ({ managerId }) => {
    const [isLoading, setIsLoading] = useState(false);

    const handleDownload = async () => {
        try {
            setIsLoading(true);
            const clients = await getClientsByManager(managerId);

            const data = await Promise.all(
                clients.map(async (client) => {
                    const [userData, preferences] = await Promise.all([
                        getClientUser(client.id),
                        getClientPreferences(client.id)
                    ]);

                    return {
                        ...client,
                        ...userData,
                        preferences
                    };
                })
            );
console.log(data)
            const worksheet = utils.json_to_sheet(
                data.map(client => ({
                    'ФИО': client.fullName,
                    'Телефон': client.phone || 'Не указано',
                    'Email': client.email || 'Не указано',
                    'Предпочтения': client.preferences.join(', '),
                }))
            );

            const workbook = utils.book_new();
            utils.book_append_sheet(workbook, worksheet, 'Клиенты');

            // 4. Скачиваем файл
            writeFile(workbook, `clients_report_${new Date().toISOString().slice(0,10)}.xlsx`);

        } catch (error) {
            console.error('Ошибка:', error);
            alert('Не удалось сформировать отчет');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <button
            onClick={handleDownload}
            disabled={isLoading}
            style={{
                padding: '10px 20px',
                background: isLoading ? '#ccc' : '#2196F3',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
            }}
        >
            {isLoading ? 'Формируем отчет...' : 'Скачать отчет в Excel'}
        </button>
    );
};

const getClientUser = async (clientId) => {
    try {
        const userData = await getUserByClientId(clientId);

        // Формируем полное имя
        const fullNameParts = [
            userData.lastname,
            userData.firstname,
            userData.patronymic

        ].filter(Boolean); // Удаляем пустые/undefined значения

        return {
            fullName: fullNameParts.join(' ') || 'Не указано',
            phone: userData.phone,
            email: userData.email
        };
    } catch (e) {
        console.error(`Ошибка для клиента ${clientId}:`, e);
        return {
            fullName: 'Не указано',
            phone: 'Не указано',
            email: 'Не указано'
        };
    }
};

// Вспомогательная функция для получения предпочтений
const getClientPreferences = async (clientId) => {
    try {
        const preferences = await getHousingPreferences(clientId);
        return preferences.map(p => p.name);
    } catch (e) {
        console.error(`Ошибка для клиента ${clientId}:`, e);
        return ['не указано'];
    }
};

export default ExcelReportButton;
