import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Для получения параметра ID из URL
import { getCurrentProperty } from '../api/propertyApi'; // Подключение API

const PropertyDetails = () => {
    const { id } = useParams(); // Получаем ID из URL
    const [property, setProperty] = useState(null);

    useEffect(() => {
        const fetchProperty = async () => {
            const data = await getCurrentProperty(id);
            setProperty(data);
        };

        fetchProperty();
    }, [id]);

    if (!property) {
        return <div>Загрузка...</div>;
    }

    return (
        <div className="property-details">
            <h1>{property.propertyType}</h1>
            <p>{property.description}</p>
            <p>{`Цена: ${property.price} руб.`}</p>
            {/* Другие подробности */}
        </div>
    );
};

export default PropertyDetails;
