import {useEffect, useState} from "react";
import {getPendingRequests} from "../api/accessProperty";
import PropertyCard from '../components/PropertyCard'; // Предположим, ваш компонент уже в проекте

const Applications = () => {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const data = await getPendingRequests(); // Данные с клиентом и квартирой
                setRequests(data);
            } catch (error) {
                setError("Ошибка при загрузке заявок: " + error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Заявки</h2>

        </div>
    );
};

export default Applications;
