import React, { useEffect, useState } from "react";
import {getPendingRequests, updateStatus} from "../api/accessProperty";
import PropertyCard from "../components/PropertyCard";
import {getPropertyEnums} from "../api/propertyService";
import { useLocation } from 'react-router-dom';

const Applications = () => {
    const [requests, setRequests] = useState([]);

    const [statusList, setStatusList] = useState([]);
    const [selectedStatus, setSelectedStatus] = useState('');

    useEffect(() => {
        if (isOnApplicationPage) {
            getPropertyEnums().then((data) => {
                setStatusList(data.requestStatuses  || []);
            });
        }
    }, [isOnApplicationPage]);


    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await getPendingRequests();
                setRequests(response); // Сохраняем заявки в состояние
                console.log("PENDING заявки:", response);
            } catch (error) {
                console.error("Ошибка при загрузке заявок:", error);
            }
        };

        fetchData();
    }, []);

    if (!requests.length) {
        return <div>Загрузка...</div>;
    }
    const handleUpdateStatus = async () => {
        try {
            await updateStatus(property.id, selectedStatus);
            alert(`Статус обновлён на ${selectedStatus}`);

        } catch (error) {
            console.log(selectedStatus, property.id)

            alert('Ошибка при обновлении статуса');
        }

    };

    return (
        <div>
            <h2>Заявки на квартиры</h2>
            <div className="applications-list">
                {requests.map((request) => (
                    <div key={request.property.id} className="application-item">
                        <PropertyCard property={request.property} />
                        {isOnApplicationPage && (
                            <div className="property-actions">
                                <select
                                    value={selectedStatus}
                                    onChange={(e) => setSelectedStatus(e.target.value)}
                                    className="status-dropdown"
                                >
                                    <option value="">Выберите статус</option>
                                    {statusList.map((status) => (
                                        <option key={status} value={status}>
                                            {status}
                                        </option>
                                    ))}
                                </select>
                                <button
                                    className="property-action-button"
                                    onClick={handleUpdateStatus}
                                    disabled={!selectedStatus}
                                >
                                    Изменить статус
                                </button>
                            </div>
                        )}
                        <div className="client-info">
                            <h4>Информация о клиенте</h4>
                            <p><strong>Имя:</strong> {request.client.authUser.firstname} {request.client.authUser.lastname}</p>
                            <p><strong>Телефон:</strong> {request.client.phone}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Applications;
