// PreferenceEditModal.js (simplified version)
import React, { useState } from 'react';

const PreferenceEditModal = ({ preference, onSave, onCancel }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [preferencesData, setPreferencesData] = useState(null);

    // Функция для открытия модального окна с данными
    const handleEditClick = (preference) => {
        setPreferencesData(preference); // Заполняем поля из существующих данных
        setIsModalOpen(true); // Открываем модальное окно
    };

    // Функция для закрытия модального окна
    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            {/* Кнопка для редактирования предпочтений */}
            <button onClick={() => handleEditClick(preference)}>Редактировать предпочтение</button>

            {/* Модальное окно для редактирования */}
            {isModalOpen && (
                <PreferenceEditModal
                    preferencesData={preferencesData}
                    onSave={handleEditPreferences} // Передаем функцию для сохранения изменений
                    onClose={handleCloseModal} // Функция для закрытия модального окна
                />
            )}
        </div>
    );
};

export default PreferenceEditModal;
