import React, { useEffect, useState } from 'react';
import { searchProperties } from '../api/propertyApi';
import PropertyCard from '../components/PropertyCard';

const CatalogPage = () => {
    const [properties, setProperties] = useState([]);
    const [filters, setFilters] = useState({
        city: '',
        minRooms: '',
        maxRooms: '',
        minPrice: '',
        maxPrice: '',
        propertyType: ''
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFilters((prev) => ({ ...prev, [name]: value }));
    };

    const handleSearch = async () => {
        setLoading(true);
        setError(null);
        try {
            const nonEmptyParams = Object.fromEntries(
                Object.entries(filters).filter(([_, value]) => value !== '')
            );
            const data = await searchProperties(nonEmptyParams);
            setProperties(data);
        } catch (err) {
            console.error(err);
            setError('Ошибка при поиске');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="page-container">
            <h1>Каталог</h1>

            {/* Форма фильтрации */}
            <div className="filter-form">
                <input type="text" name="city" placeholder="Город" value={filters.city} onChange={handleChange} />
                <input type="number" name="minRooms" placeholder="Мин. комнат" value={filters.minRooms} onChange={handleChange} />
                <input type="number" name="maxRooms" placeholder="Макс. комнат" value={filters.maxRooms} onChange={handleChange} />
                <input type="number" name="minPrice" placeholder="Мин. цена" value={filters.minPrice} onChange={handleChange} />
                <input type="number" name="maxPrice" placeholder="Макс. цена" value={filters.maxPrice} onChange={handleChange} />
                <select name="propertyType" value={filters.propertyType} onChange={handleChange}>
                    <option value="">Тип недвижимости</option>
                    <option value="APARTMENT">Квартира</option>
                    <option value="HOUSE">Дом</option>
                    <option value="COMMERCIAL">Коммерческая</option>
                    {/* Добавь другие типы по своему Enum */}
                </select>
                <button onClick={handleSearch}>Поиск</button>
            </div>

            {/* Результаты */}
            {loading && <p>Загрузка...</p>}
            {error && <p>{error}</p>}
            <div className="property-list">
                {properties.map((p) => (
                    <PropertyCard key={p.id} property={p} />
                ))}
            </div>
        </div>
    );
};

export default CatalogPage;
