import { useEffect, useState } from "react";
import { getClientPropertyAccess } from "../api/AccessService";
import PropertyCard from "./PropertyCard";
import SkeletonPropertyCard from "./SkeletonPropertyCard";

const AccessiblePropertiesList = ({ clientId }) => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchProperties = async () => {
            try {
                const response = await getClientPropertyAccess(clientId);
                setProperties(response.data);
            } catch (error) {
                console.error("Ошибка при получении доступных квартир:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchProperties();
    }, [clientId]);

    if (loading) {
        return (
            <div className="properties-list">
                {Array(3).fill(0).map((_, i) => (
                    <SkeletonPropertyCard key={i} />
                ))}
            </div>
        );
    }

    if (properties.length === 0) {
        return <p>Нет доступных квартир.</p>;
    }

    return (
        <div className="properties-list">
            {properties.map(property => (
                <PropertyCard key={property.id} property={property} />
            ))}
        </div>
    );
};

export default AccessiblePropertiesList;
