import { useEffect, useState } from "react";
import { getPendingRequests } from "../api/accessProperty";
import PropertyCard from "../components/PropertyCard";

const Applications = () => {
    const [requests, setRequests] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await getPendingRequests();
                setRequests(response); // Сохраняем заявки в состояние
                console.log("PENDING заявки:", response);
            } catch (error) {
                console.error("Ошибка при загрузке заявок:", error);
            }
        };

        fetchData();
    }, []);

    if (!requests.length) {
        return <div>Загрузка...</div>;
    }

    return (
        <div>
            <h2>Заявки на квартиры</h2>
            <div className="applications-list">
                {requests.map((request) => (
                    <div key={request.property.id} className="application-item">
                        <PropertyCard property={request.property} /> {/* Отображаем карточку недвижимости */}
                        <div className="client-info">
                            <h4>Информация о клиенте</h4>
                            <p><strong>Имя:</strong> {request.client.authUser.firstname} {request.client.lastname}</p>
                            <p><strong>Телефон:</strong> {request.client.phone}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Applications;
