import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {getPropertyById, updateProperty} from '../api/propertyService';
import {getClientById, getClientsByManager} from '../api/ClientService'; // Импортируем функцию API
import { useAuth } from '../context/AuthContext';
import {createTravelRequest, grantAccessToProperty} from "../api/accessProperty";

const PropertyDetailPage = () => {
    const { id } = useParams();
    const { user } = useAuth(); // Получаем текущего пользователя
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [clients, setClients] = useState([]); // Список клиентов
    const [selectedClientId, setSelectedClientId] = useState('');
    const [clientData, setClientData] = useState(null);

    const isManager = user?.role === 'MANAGER';

    useEffect(() => {
        const fetchProperty = async () => {
            try {
                const data = await getPropertyById(id);
                setProperty(data);
            } catch (err) {
                setError('Ошибка при загрузке данных');
            } finally {
                setLoading(false);
            }
        };

        fetchProperty();
    }, [id]);

    useEffect(() => {
        if (isManager && user?.id) {
            const fetchClients = async () => {
                try {
                    const data = await getClientsByManager(user.id);
                    setClients(data);
                } catch (err) {
                    console.error('Ошибка при получении клиентов:', err);
                }
            };
            fetchClients();
        }
    }, [isManager, user]);

    useEffect(() => {
        const fetchClientData = async () => {
            const clientId = localStorage.getItem('clientId');
            if (clientId) {
                try {
                    const data = await getClientById(clientId);
                    setClientData(data);
                } catch (err) {
                    console.error('Ошибка при получении данных клиента:', err);
                }
            }
        };

        fetchClientData();
    }, []);


    const handleClientChange = (e) => {
        setSelectedClientId(e.target.value);
    };

    const handleAssign = async () => {
        if (!selectedClientId || !property?.id || !user?.id) return;
        const managerId = user.id;

        try {
            // Вызываем grantAccessToProperty с параметрами managerId, clientId и propertyId
            await grantAccessToProperty(managerId, selectedClientId, property.id);
            alert('Доступ успешно выдан клиенту!');
        } catch (error) {
            alert('Ошибка при выдаче доступа');
        }
    };


    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="property-detail-page">
            <h1>Детали недвижимости</h1>
            {property ? (
                <div>
                    <h2>{property.title}</h2>
                    <p>Город: {property.city}</p>
                    <p>Район: {property.district}</p>
                    <p>Улица: {property.street}</p>
                    <p>Цена: {property.price}</p>
                    <p>Количество комнат: {property.rooms}</p>
                    <p>Этаж: {property.floor}</p>
                    <p>Балкон: {property.hasBalcony ? 'Да' : 'Нет'}</p>
                    <p>Тип: {property.propertyType}</p>
                    <p>Животные: {property.allowPets ? 'Разрешены' : 'Запрещены'}</p>

                    {isManager && (
                        <div className="form-group" style={{ marginTop: '20px' }}>
                            <label>Клиент:</label>
                            <select
                                value={selectedClientId}
                                onChange={handleClientChange}
                                required
                            >
                                <option value="">-- Выберите клиента --</option>
                                {clients.map(client => (
                                    <option key={client.id} value={client.id}>
                                        {client.authUser
                                            ? `${client.authUser.lastname} ${client.authUser.firstname}`
                                            : `Клиент #${client.id}`}
                                    </option>
                                ))}
                            </select>
                            <button onClick={handleAssign} disabled={!selectedClientId}>
                                Присвоить
                            </button>
                        </div>
                    )}

                    {!isManager && clientData?.accessLevel === 'TRAVELER' && property?.propertyLevel === 'TRAVEL' && (
                        <div style={{ marginTop: '20px' }}>
                            <button onClick={async () => {
                                try {
                                    await createTravelRequest(clientData.id, property.id);
                                    alert('Запрос успешно отправлен');
                                } catch (e) {
                                    alert('Ошибка при отправке запроса');
                                }
                            }}>
                                Отправить запрос
                            </button>
                        </div>
                    )}
                    {isManager && property && (
                        <div style={{ marginTop: '40px' }}>
                            <h3>Редактировать недвижимость</h3>
                            <form onSubmit={async (e) => {
                                e.preventDefault();

                                try {
                                    const updatedData = {
                                        rooms: property.rooms,
                                        price: property.price,
                                        floor: property.floor,
                                        hasBalcony: property.hasBalcony,
                                        allowPets: property.allowPets,
                                        description: property.description,
                                        propertyLevel: property.propertyLevel,
                                        propertyType: property.propertyType,
                                        status: property.status
                                    };

                                    const updated = await updateProperty(property.id, updatedData);
                                    alert('Недвижимость успешно обновлена');
                                    setProperty(updated);
                                } catch (err) {
                                    alert('Ошибка при обновлении');
                                }
                            }}>
                                <label>Цена:
                                    <input
                                        type="number"
                                        value={property.price}
                                        onChange={(e) => setProperty({ ...property, price: parseFloat(e.target.value) })}
                                    />
                                </label>

                                <br />

                                <label>Этаж:
                                    <input
                                        type="number"
                                        value={property.floor}
                                        onChange={(e) => setProperty({ ...property, floor: parseInt(e.target.value) })}
                                    />
                                </label>

                                <br />

                                <label>Балкон:
                                    <select
                                        value={property.hasBalcony}
                                        onChange={(e) => setProperty({ ...property, hasBalcony: e.target.value === 'true' })}
                                    >
                                        <option value="true">Да</option>
                                        <option value="false">Нет</option>
                                    </select>
                                </label>

                                <br />

                                <label>Животные:
                                    <select
                                        value={property.allowPets}
                                        onChange={(e) => setProperty({ ...property, allowPets: e.target.value === 'true' })}
                                    >
                                        <option value="true">Разрешены</option>
                                        <option value="false">Запрещены</option>
                                    </select>
                                </label>

                                <br />

                                <label>Описание:
                                    <textarea
                                        value={property.description || ''}
                                        onChange={(e) => setProperty({ ...property, description: e.target.value })}
                                    />
                                </label>

                                <br />

                                <label>Тип недвижимости:
                                    <select
                                        value={property.propertyType}
                                        onChange={(e) => setProperty({ ...property, propertyType: e.target.value })}
                                    >
                                        <option value="FLAT">Квартира</option>
                                        <option value="HOUSE">Дом</option>
                                        <option value="ROOM">Комната</option>
                                    </select>
                                </label>

                                <br />

                                <label>Уровень:
                                    <select
                                        value={property.propertyLevel}
                                        onChange={(e) => setProperty({ ...property, propertyLevel: e.target.value })}
                                    >
                                        <option value="BASIC">BASIC</option>
                                        <option value="STANDARD">STANDARD</option>
                                        <option value="PREMIUM">PREMIUM</option>
                                        <option value="TRAVEL">TRAVEL</option>
                                    </select>
                                </label>

                                <br />

                                <label>Статус:
                                    <select
                                        value={property.status}
                                        onChange={(e) => setProperty({ ...property, status: e.target.value })}
                                    >
                                        <option value="AVAILABLE">Доступна</option>
                                        <option value="UNAVAILABLE">Недоступна</option>
                                    </select>
                                </label>

                                <br />
                                <button type="submit">Сохранить изменения</button>
                            </form>
                        </div>
                    )}


                </div>
            ) : (
                <p>Информация о недвижимости не найдена</p>
            )}
        </div>
    );
};

export default PropertyDetailPage;
