import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';
import { ROLES } from '../../constants/roles';
import "../../styles/Header.css"; // Стили для компонента

const Header = () => {
    const { isAuthenticated, user, logout } = useAuth();

    return (
        <header className="header">
            <div className="header-container">
                <nav className="header-nav">
                    <span className="header-title">Agency App</span>



                </nav>
                {isAuthenticated ? (
                    <div className="header-auth-container">
                        {user?.role === ROLES.ADMIN && (
                            <Link to="/admin-dashboard" className="header-button ">
                                Админ
                            </Link>
                        )}
                        {user?.role === ROLES.MANAGER && (
                            <Link to="/manager-dashboard" className="header-button ">
                                Менеджер
                            </Link>
                        )}
                        {user?.role === ROLES.CLIENT && (
                            <Link to="/client-dashboard" className="header-button ">
                                Клиент
                            </Link>
                        )}
                        {/*<span className="header-user">*/}
                        {/*    {user?.firstname} {user?.lastname}*/}
                        {/*</span>*/}
                        <button className = "header-button"> <span>о компании</span></button>
                        <button className = "header-button"> <span>каталог</span></button>
                        <button className = "header-button"> <span>путешественники</span></button>



                    </div>

                ) : (
                    <Link to="/login" className="header-link">
                        Вход
                    </Link>
                )}
                <button onClick={logout} className="header-button">
                    Выйти
                </button>
            </div>
        </header>
    );
};

export default Header;
