import React, { useEffect, useState } from 'react';
import { getClientsByManager } from '../api/ClientService'; // путь адаптируйте под свой проект

const ClientList = ({ managerId }) => {
    const [clients, setClients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClients = async () => {
            try {
                const data = await getClientsByManager(managerId);
                setClients(data);
            } catch (err) {
                setError('Ошибка при загрузке клиентов');
            } finally {
                setLoading(false);
            }
        };

        if (managerId) {
            fetchClients();
        }
    }, [managerId]);

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Список клиентов</h2>
            {clients.length === 0 ? (
                <p>Клиенты не найдены.</p>
            ) : (
                <ul>
                    {clients.map(client => (
                        <li key={client.id}>
                            <strong>Телефон:</strong> {client.phone} <br />
                            <strong>Паспорт:</strong> {client.passport} <br />
                            <strong>Уровень доступа:</strong> {client.accessLevel}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ClientList;
