import React from 'react';
import PropTypes from 'prop-types';
import '../styles/PropertyCard.css'; // Создадим отдельный файл стилей
import { Link } from 'react-router-dom';


const PropertyCard = ({ property }) => {
    return (
        <div className="property-card">
            <div className="property-image">
                {/* Здесь можно разместить изображение */}
                <div className="image-placeholder">
                    {property.propertyType?.charAt(0) || 'P'}
                </div>
            </div>
            <div className="property-details">
                <h3 className="property-type">
                    {property.propertyType || 'Тип не указан'}
                </h3>
                <p className="property-description">
                    {property.description || 'Описание отсутствует'}
                </p>
                <div className="property-price">
                    {property.price ? `${property.price} ₽` : 'Цена не указана'}
                </div>
            </div>
        </div>
    );
};

PropertyCard.propTypes = {
    property: PropTypes.shape({
        id: PropTypes.number,
        price: PropTypes.number,
        propertyType: PropTypes.string,
        description: PropTypes.string,
        // Добавьте другие необходимые свойства
    }).isRequired
};

export default PropertyCard;
